import React from "react";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import GridList from "components/grid-list";
import DishListItem from "components/grid-list-item-dish";
import "./dish-categories.css";

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 8 * 3 }}>
      {props.children}
    </Typography>
  );
}

TabContainer.propTypes = {
  children: PropTypes.node.isRequired
};

export default class SimpleTabs extends React.Component {
  state = {
    value: 0
  };

  handleChange = (event, value) => {
    const { categories } = this.props;
    this.setState({ value });
    this.props.handleMenu(value ? categories[value - 1].id : 0);
  };

  renderTabs = categories => {
    if (!categories) return [];
    return categories.map(({ id, CategoryName }) => {
      return <Tab key={id} label={CategoryName} />;
    });
  };

  render() {
    const { value } = this.state;
    const { categories, dishes } = this.props;

    return (
      <div className="categories">
        <AppBar position="static">
          <Tabs value={value} onChange={this.handleChange}>
            <Tab key={0} label="All" />
            {this.renderTabs(categories)}
          </Tabs>
        </AppBar>
        <TabContainer>
          <GridList
            data={dishes}
            GridListItem={DishListItem}
            onDishAddClick={this.props.onDishAddClick}
          />
        </TabContainer>
      </div>
    );
  }
}
