import React from 'react';

import GridListTileBar from '@material-ui/core/GridListTileBar';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import PropTypes from 'prop-types';
import { withStyles,createStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

import ListSubheader from '@material-ui/core/ListSubheader';

import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';

import './grid-list-item.css'

const theme = createMuiTheme({
  palette: {
    primary: {
       main: '#FAFAFA',
     },
     secondary: {
       main: '#212121',
     }, // This is just green.A700 as hex.
  },
});


const styles = theme =>
createStyles({
  button: {
    margin: theme.spacing.unit,
    borderColor: 'white'
  },
  priceBar: {
    display: "flex",
    width: "2"
  },
  extendedIcon: {
    marginRight: theme.spacing.unit,
  },
});



const GridListItem = ({id, name, price, description, classes}) => {

  return (
  <div className='list-item'>
    <img src={`/images/dishes/${id}.jpg`} alt={name} />
    <ListSubheader component="div">December</ListSubheader>
    <GridListTileBar
      title={name}
      subtitle={
                <span className="description">
                  {description}
                </span>}
      actionIcon={
      <MuiThemeProvider theme={theme}>
        <ListSubheader component="div" className="{classes.priceBar}">
          <Typography variant="subtitle1" color="primary">
              {`${price}$ `}
           </Typography>
          <Button  variant="outlined" aria-label="Add" color="primary" className={classes.button}>
          Add
          </Button>
        </ListSubheader>
      </MuiThemeProvider>
      }
    />
</div>
  )
}

GridListItem.propTypes = {
  classes: PropTypes.object.isRequired,
};


export default withStyles(styles)(GridListItem);
