import React from "react";

import GridListTileBar from "@material-ui/core/GridListTileBar";
import Button from "@material-ui/core/Button";
import PropTypes from "prop-types";
import { withStyles, createStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import * as R from "ramda";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import "./grid-list-item.css";
import { DISH_PHOTOS_URL } from "shared-components/constants";

const theme = createMuiTheme({
  typography: {
    useNextVariants: true
  },
  palette: {
    primary: {
      main: "#FAFAFA"
    },
    secondary: {
      main: "#BDBDBD"
    } // This is just green.A700 as hex.
  }
});

const styles = theme =>
  createStyles({
    button: {
      margin: theme.spacing.unit,
      borderColor: "white"
    },
    tileBar: {
      height: "auto",
      padding: "0.5rem"
    },
    priceBar: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center"
    },
    extendedIcon: {
      marginRight: theme.spacing.unit
    }
  });

class GridListItem extends React.Component {
  render() {
    const { classes, onDishAddClick, ...receivedDish } = this.props;
    const {
      id,
      DishName: Name,
      DishDescription: Description,
      defaultPhoto,
      dish_sizes: Sizes,
      RestaurantName
    } = receivedDish;
    const dish = { id, Name, Description, Sizes, RestaurantName, defaultPhoto };
    const shortDescription = `${R.take(140, Description)}${
      Description.length > 140 ? "..." : ""
    }`;

    return (
      <div className="list-item">
        <img src={`${DISH_PHOTOS_URL + defaultPhoto}`} alt={Name} />
        <MuiThemeProvider theme={theme}>
          <GridListTileBar
            className={classes.tileBar}
            title={
              <div>
                <Typography
                  variant="subtitle2"
                  color="secondary"
                  gutterBottom
                  noWrap
                >
                  "{RestaurantName}" restaurant
                </Typography>
                <Typography variant="h6" color="primary" gutterBottom noWrap>
                  {Name}
                </Typography>
              </div>
            }
            subtitle={<div className="description">{shortDescription}</div>}
            actionIcon={
              <div className={classes.priceBar}>
                <Typography variant="subtitle1" color="primary">
                  {`$${Sizes[0].price}`}
                </Typography>
                <Button
                  variant="outlined"
                  onClick={() => onDishAddClick(dish)}
                  aria-label="Add"
                  color="primary"
                  className={classes.button}
                >
                  Add
                </Button>
              </div>
            }
          />
        </MuiThemeProvider>
      </div>
    );
  }
}

GridListItem.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(GridListItem);
