import React, { Component } from "react";

import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import "../account-page.css";
import { getCurrentCustomer } from "api";

export default class AccountSettings extends Component {
  state = {
    save: false,
    disabled: true,
    user: {}
  };
  componentDidMount() {
    getCurrentCustomer().then(user => {
      this.setState({ user });
    });
  }
  saveButton = () => {
    const oldState = { ...this.state };
    return this.setState({
      save: !oldState.save,
      disabled: !oldState.disabled
    });
  };
  render() {
    const saveButton = this.state.save ? "Save" : "Edit";
    if (!this.state.user) return <Redirect to="/login" />;
    const { CustName, CustSurname, CustEmail, CustPhone } = this.state.user;
    return (
      <Paper className="account-setting" elevation={15}>
        <div className="setting-form">
          <TextField
            disabled={this.state.disabled}
            className="input"
            label="Name"
            margin="normal"
            variant="outlined"
            type="text"
            defaultValue="Illia"
          />
          <TextField
            disabled={this.state.disabled}
            className="input"
            label="Surname"
            margin="normal"
            variant="outlined"
            type="text"
            defaultValue="Sydun"
          />
          <TextField
            disabled={this.state.disabled}
            className="input"
            label="Email"
            margin="normal"
            variant="outlined"
            type="email"
            defaultValue="illia.sydun@gmail.com"
          />
          <TextField
            disabled={this.state.disabled}
            className="input"
            label="Phone Nubmer"
            margin="normal"
            variant="outlined"
            type="text"
            defaultValue="733 423 688"
          />
          <TextField
            disabled={this.state.disabled}
            className="input"
            label="Password"
            margin="normal"
            variant="outlined"
            type="password"
            defaultValue="illia.sydun"
          />
        </div>
        <div className="setting-form-buttons">
          <Button className="button" onClick={this.saveButton}>
            {saveButton}
          </Button>
        </div>
      </Paper>
    );
  }
}
