import React, {Component} from 'react';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import SelectControl from 'components/select-control'



export default class SeatsPicker extends React.Component {
  state = {
    open: false,
    seats: ''
  };

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };


render() {

  return (
    <div>
        <Select
          value={this.state.seats}
          onChange={this.handleChange}
          inputProps={{
            name: 'seats'
          }}
        >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={1}>{`Table for 1`}</MenuItem>
            <MenuItem value={2}>{`Table for 2`}</MenuItem>
            <MenuItem value={3}>{`Table for 3`}</MenuItem>
            <MenuItem value={4}>{`Table for 4`}</MenuItem>
            <MenuItem value={5}>{`Table for 5`}</MenuItem>
            <MenuItem value={6}>{`Table for 6`}</MenuItem>
            <MenuItem value={7}>{`Table for 7`}</MenuItem>
        </Select>
    </div>
  );
 }
};
