import React from "react";

import "./navigation-list.css";
import NavigationListItem from "../navigation-list-item";
import { getLoggedInUser } from "api";
import client from "feathers/client";

export default class Header extends React.Component {
  state = {};

  render() {
    client.on("logout", () => {
      this.setState({ user: undefined });
    });
    client.on(`authenticated`, authResult => {
      const user = authResult
        .then(payload => {
          console.log("JWT Payload", payload);
          return client.service("users").get(payload.userId);
        })
        .then(user => {
          client.set("user", user);
          console.log("User", client.get("user"));
        });
      console.log("userrr", authResult);
      this.setState({ user });
    });
    const navItemsGuest = [
      { label: "Sign in", link: "/login" },
      { label: "Sign up", link: "/registration" }
    ];
    const navItemsSignedIn = [{ label: "Account", link: "/account" }];
    const navItems = this.state.user ? navItemsSignedIn : navItemsGuest;
    console.log("rendering", navItems);
    console.log("rendering user", this.state.user);

    return (
      <div className="navigation-list">
        <ul>
          {navItems.map(item => {
            return (
              <NavigationListItem
                item={item}
                key={item.label}
                user={this.state.user}
              />
            );
          })}
        </ul>
      </div>
    );
  }
}
