import {computed, decorate, observable} from "mobx";

class CartStore {
    cart = localStorage.getItem("cart");
    expanded = false;
    expand = () => this.expanded = !this.expanded
}

decorate(CartStore, {
    cart: observable,
    expanded: observable,
    expand: computed
});
export default CartStore;
