import React from 'react';

import SelectControl from 'components/select-control'


const seatsNumbers = [
  { label: 'Table for 1'},
  { label: 'Table for 2'},
  { label: 'Table for 3'},
  { label: 'Table for 4'},
  { label: 'Table for 5'},
  { label: 'Table for 6'},
  { label: 'Table for 7'},
].map(item => ({
  value: item.label,
  label: item.label,
  })
);

export default class SeatsPicker extends React.Component {

render() {

  return (
        <SelectControl items = {seatsNumbers} emptyValue = 'Select number of seats'/>
  );
 }
};
