import React from 'react';

import GridListTileBar from '@material-ui/core/GridListTileBar';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';

import './grid-list-item.css'

const theme = createMuiTheme({
  palette: {
    primary: {
       main: '#FAFAFA',
     },
     secondary: {
       main: '#212121',
     }, // This is just green.A700 as hex.
  },
});


const styles = theme => ({
  button: {
    margin: theme.spacing.unit,
    borderColor: 'white'
  },
  extendedIcon: {
    marginRight: theme.spacing.unit,
  },
});


const GridListItem = ({id, name, price, description, classes}) => {

  return (
  <div className='list-item'>
    <img src={`/images/dishes/${id}.jpg`} alt={name} />
    <GridListTileBar
      title={name}
      subtitle={<span>{`${price}$`}</span>}
      actionIcon={
      <MuiThemeProvider theme={theme}>
        <Button  variant="outlined" aria-label="Add" color="primary" className={classes.button}>
          Add
        </Button>
      </MuiThemeProvider>
      }
    />
</div>
  )
}

GridListItem.propTypes = {
  classes: PropTypes.object.isRequired,
};


export default withStyles(styles)(GridListItem);
