import client from "feathers/client";

import { getDishInMenu } from "api";
import { DISH_PHOTOS_URL } from "shared-components/constants";

export const getPhotoByDishId = Dish_id =>
  client.service("dish-photos").findOne({ query: { Dish_id } });

export const getPhotoPathByDishId = Dish_id =>
  getPhotoByDishId(Dish_id).then(photo => DISH_PHOTOS_URL + photo.Path);

export const getPhotoPathByDishInMenuId = MenuDish_id =>
  getDishInMenu(MenuDish_id).then(DishInMenu =>
    getPhotoPathByDishId(DishInMenu.rest_dish_size.RestaurantDish_id)
  );
