import React, { Component } from "react";
import { Link } from "react-router-dom";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import client from "feathers/client";
import {authStore} from "store";
import {decorate} from "mobx";
import {observer} from "mobx-react";

class NavigationListItem extends Component {
  state = {
    anchorEl: null,
    auth: false,
    user: null
  };

  handleClick = event => {
    this.setState({ anchorEl: event.currentTarget });
    authStore.user.CustName = 'Bob'
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const { anchorEl } = this.state;
    const { item } = this.props;
    if (localStorage.getItem("feathers-jwt")) {
      const user = client.get("user");
      return (
        <li>
          <span className="logged-username">{authStore.user && authStore.user.CustName}</span>
          <IconButton
            aria-label="More"
            aria-owns={anchorEl ? "long-menu" : undefined}
            aria-haspopup="true"
            onClick={this.handleClick}
            color="primary"
          >
            <MoreVertIcon />
          </IconButton>
          <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={this.handleClose}
          >
            <Link to="/account">
              <MenuItem>My Account</MenuItem>
            </Link>
            <MenuItem
              onClick={() => {
                this.handleClose();
                client.logout();
                authStore.isAuthenticated(false)
              }}
            >
              Logout
            </MenuItem>
          </Menu>
        </li>
      );
    } else {
      return (
        <li>
          <Link to={item.link}>
            <Button className="button">{item.label}</Button>
          </Link>
        </li>
      );
    }
  }
}
export default observer(NavigationListItem)
