import client from "feathers/client";
export var getAllDishes = function getAllDishes() {
  return client.service("restaurant-dishes").find().then(function (res) {
    return res.data;
  });
};
export var getDishesByRestId = function getDishesByRestId(Restaurant_id) {
  return client.service("restaurant-dishes").find({
    query: {
      Restaurant_id: Restaurant_id
    }
  }).then(function (res) {
    return res.data;
  });
};
export var getDish = function getDish(Dish_id) {
  return client.service("restaurant-dishes").findOne({
    query: {
      Dish_id: Dish_id
    }
  });
};
export var getDishesByDishCategoryId = function getDishesByDishCategoryId(Category_id) {
  return getAllDishes().then(function (res) {
    return res.filter(function (resDish) {
      return resDish.Category_id === Category_id;
    });
  });
};
