import {action, computed, decorate, observable} from "mobx";

class CartStore {
    cart = null;
    expanded = false;
    length = null;
    expand () {
        this.expanded = !this.expanded;
    }
    setLength (length) {
        this.length =  length
    }
    updateCart (cart){
        this.cart = cart
    }
}

decorate(CartStore, {
    cart: observable,
    expanded: observable,
    expand: action,
    setLength: action,
    updateCart: action
});
export default CartStore;
