import {action, computed, decorate, observable} from "mobx";

class CartStore {
    cart = localStorage.getItem("cart");
    expanded = false;
    length = null;
    expand () {
        this.expanded = !this.expanded;
    }
    count () {
        this.length =  this.cart.dishes.length
    }
}

decorate(CartStore, {
    cart: observable,
    expanded: observable,
    expand: action,
    count: action
});
export default CartStore;
