import client from "feathers/client";

export const getAllProducts = () =>
  client
    .service("products")
    .find()
    .then(res => res.data);

export const getProductCategories = () =>
  client
    .service("product-categories")
    .find()
    .then(res => res.data);

export const getProductsByDishInSI = DishInScheduleItem_id =>
  client
    .service("additional-products-for-item")
    .find({ query: { DishInScheduleItem_id } })
    .then(res => res.data);

export const getProduct = (DishInScheduleItem_id, Product_id) =>
  client
    .service("additional-products-for-item")
    .findOne({ query: { DishInScheduleItem_id, Product_id } });

export const createAddlProductForSI = (DishInScheduleItem_id, Product_id) =>
  client
    .service("additional-products-for-item")
    .create({ DishInScheduleItem_id, Product_id });
