import client from "feathers/client";

export const getAllPromotions = () =>
  client
    .service("promotions")
    .find()
    .then(res => res.data);

export const getPromotion = id =>
  client.service("promotions").findOne({
    query: {
      id
    }
  });

export const getPromotionByCode = Promocode =>
  client.service("promotions").findOne({
    query: {
      Promocode
    }
  });
