import React from "react";
import Button from "@material-ui/core/Button";
import {authStore} from "store";
import {Link} from "react-router-dom";

const FooterItem = ({ item }) => {
  if( item.label === "For business"){
    return <Link to='/for-business/registration'><Button className="button" onClick={() => authStore.setManager(!authStore.isManager)}>{item.label}</Button></Link>
  }
  if( item.label === "About us"){
    return <Button className="button" onClick={() => authStore.setManager(!authStore.isManager)}>{item.label}</Button>
  }
  return <Button className="button">{item.label}</Button>;
};
export default FooterItem;
