import React, { Component } from "react";

import Header from "../../../shared-components/header";
import Choice from "./choice";
import Footer from "./footer";

import "./main-page.css";
import {observer} from "mobx-react";
import {authStore} from "store";

class MainPage extends Component {
  render() {
    const choiceItems = authStore.isManager ? [
      { label: "Restaurant", classN: "restaurant", link: "restaurants/2" },
      { label: "Staff", classN: "staff", link: "dishes" }
    ] : [
      { label: "Restaurants", classN: "restaurants", link: "restaurants" },
      { label: "Food", classN: "food", link: "dishes" }
    ];
    const footerItems = [
      { label: "About us" },
      { label: "FAQ" },
      { label: "Privacy policy" },
      { label: "For business" },
    ];

    return (
      <div className="main_page">
        <Header />
        <Choice choiceItems={choiceItems} />
        <Footer footerItems={footerItems} />
      </div>
    );
  }
}

export default observer(MainPage)
