import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";

const styles = theme => ({
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper
  }
});

function WeekDaysList(props) {
  const { classes, weekDays } = props;
  return (
    <List component="div" className={classes.root}>
      {weekDays.map(weekDay => (
        <ListItem key={weekDay.Day} alignItems="center" divider button>
          <ListItemText
            primary={`${weekDay.Opened} - ${weekDay.Closed}`}
            secondary={weekDay.DayName}
          />
        </ListItem>
      ))}
    </List>
  );
}

WeekDaysList.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(WeekDaysList);
