import React from "react";
import "./restaurant-page.css";
import WeekSchedule from "components-vitalii/week-schedule";
import DishPage from "components/pages/dish-page";

export default ({ data }) => {
  const { restaurant, openDish,handleDishClose, dishToShow, MenuView } = data;
  const { RestaurantName: name, RestaurantDescription: description, schedule, price, address, id } = restaurant;
  const {StreetName, StreetNumber, ZipCode} = address;
  const restaurantImage = `url(/images/restaurants/${id}.jpg)`;
  return (
    <div
      className="restaurant-info"
      style={{ backgroundImage: restaurantImage }}
    >
      <div className="restaurant-info-box">
        <div className="restaurant-name">
          <h2>{name}</h2>
        </div>
        <div className="restaurant-description">
          <p>{description}</p>
        </div>
        <div className="restaurant-price">
          <p>{price}</p>
        </div>
        <div className="restaurant-address">
          <p>{`${StreetName} ${StreetNumber}, ${ZipCode}`}</p>
        </div>
        <div className="scheduleButton">
          <WeekSchedule weekDays={schedule}/>
        </div>
      </div>
      <DishPage openDish={openDish} handleClose={handleDishClose} dishToShow={dishToShow} />
      {MenuView}
    </div>
  );
};
