import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';

import './filter-dialog.css';

export default class FilterDialog extends React.Component {
  state = {
    open: false
  };
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
    this.props.onDialogClose();
  };
  handleApply = () => {
    this.setState({ open: false });
    this.props.onFilterApply();
  };

  render() {
    const { dialogContent, btnName } = this.props;
    return (
      <div >
        <Button variant="outlined" onClick={this.handleClickOpen}>{btnName}</Button>
        <Dialog
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="form-dialog-title"
        >

            {dialogContent}

          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Close
            </Button>
            <Button onClick={this.handleApply} color="primary">
              Apply filter
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}
