import React from "react";

import GridListTileBar from "@material-ui/core/GridListTileBar";
import IconButton from "@material-ui/core/IconButton";
import InfoIcon from "@material-ui/icons/Info";
import { Link } from "react-router-dom";

import "./grid-list-item-restaurant.css";
import { RESTAURANTS_PHOTOS_URL } from "shared-components/constants";

const GridListItem = Restaurant => {
  const { id, RestaurantName, schedule, defaultPhoto } = Restaurant;
  const { Opened, Closed } = schedule;
  return (
    <div className="list-item">
      <Link to={`/restaurants/${id}`}>
        <img
          src={`${RESTAURANTS_PHOTOS_URL + defaultPhoto}`}
          alt={RestaurantName}
        />
        <GridListTileBar
          title={RestaurantName}
          subtitle={<span>{`${Opened} - ${Closed}`}</span>}
          actionIcon={
            <IconButton>
              <InfoIcon className="icon" />
            </IconButton>
          }
        />
      </Link>
    </div>
  );
};

export default GridListItem;
