import React from 'react';
import SelectControl from 'components/select-control'

const cities = [
  { label: 'Lublin' },
  { label: 'Warsaw' },
  { label: 'Lviv' },
  { label: 'Munchen' },
  { label: 'Barcelona' },
].map(item => ({
  value: item.label,
  label: item.label,
  })
);


export default class SeatsPicker extends React.Component {

render() {

  return (
        <SelectControl items = {cities} emptyValue = 'Select city'/>
  );
 }
};
