import React, {Component} from 'react';
import SelectControl from 'components/select-control'

const countries = [
  { label: 'Poland' },
  { label: 'Ukraine' },
  { label: 'Germany' },
  { label: 'Italy' },
  { label: 'Spain' },
].map(item => ({
  value: item.label,
  label: item.label,
}));

export default class SeatsPicker extends Component {

render() {

  return (
        <SelectControl items = {countries} emptyValue = 'Select country'/>
  );
 }
};
