import React from 'react';
import SelectControl from 'components/select-control'

const cuisines = [
  { label: `American` },
  { label: `Asian` },
  { label: `Bars` },
  { label: `British` },
  { label: `Burgers` },
  { label: `French` },
  { label: `Indian` },
  { label: `Irish` },
  { label: `Italian` },
  { label: `Japanese` }
].map(item => ({
  value: item.label,
  label: item.label,
  })
);


export default class SeatsPicker extends React.Component {

render() {

  return (
        <SelectControl items = {cuisines} emptyValue = 'Select cuisine'/>
  );
 }
};
