import React from "react";
import Chip from "@material-ui/core/Chip";
import Paper from "@material-ui/core/Paper";
import { withStyles } from "@material-ui/core";

const styles = theme => ({
  root: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    padding: theme.spacing.unit / 2
  },
  chip: {
    margin: theme.spacing.unit / 2
  }
});

function ChipsArray({ classes, items, onDelete, maxHeight, maxWidth }) {
  const maxH = maxHeight ? maxHeight : classes.root.maxHeight;
  const maxW = maxWidth ? maxWidth : classes.root.maxWidth;
  const itemsToShow = items ? items : [];
  return (
    <Paper className={classes.root} style={{ maxHeight: maxH, maxWidth: maxW }}>
      {itemsToShow.map(p => {
        return (
          <Chip
            key={p.id}
            label={p.ProductName}
            onDelete={() => onDelete(p)}
            className={classes.chip}
          />
        );
      })}
    </Paper>
  );
}

export default withStyles(styles)(ChipsArray);
