import React from "react";
import * as R from "ramda";
import Select from "shared-components/select-control";

export default class citiesSelect extends React.Component {

  render() {
      console.log("cities",this.props.cities )
    const options = R.map(city => ({value: city.id, label: city.CityName}), this.props.cities);
      console.log("options",options )
      console.log("this.props.value",this.props.value )
      return (
          <Select
              onSelectChange={(t) => this.props.handleChange(t.value)}
              name="title"
              displayEmpty={false}
              value={this.props.value}
              items={options}
              emptyValue="Select city"
          />
    );
  }
}
