import React from "react";
import { Redirect, Route } from "react-router-dom";
import {authStore} from "store";

export default ({ component: Component, ...rest }) => (
  <Route
    {...rest}
    render={props =>
      authStore.isAuthenticated ? (
        <Component {...props} />
      ) : (
        <Redirect
          to={{
            pathname: "/login",
            state: { from: props.location }
          }}
        />
      )
    }
  />
);
