import {action, decorate, observable} from "mobx";
import client from "feathers/client";

class AuthStore {
    isAuthenticated = localStorage.getItem("feathers-jwt");
    user = null;

    setUser (user) {
        this.user = user
    }
}

decorate(AuthStore, {
    isAuthenticated: observable,
    user: observable,
    setUser: action
});
export default AuthStore;