import { action, decorate, observable } from "mobx";

class AuthStore {
    isAuthenticated = null;
    user = null;
    isManager = false;

  setUser(user) {
    this.user = user;
  }
  setManager (isManager) {
        this.isManager = isManager
  }
  authenticate(user) {
    if(user) console.log("USER A", user)
    this.isAuthenticated = !!user;
    if (!user) this.setUser(null);
    else this.setUser(user)
  }
}

decorate(AuthStore, {
    isAuthenticated: observable,
    user: observable,
    isManager: observable,
    setManager: action,
    setUser: action,
    authenticate: action
});
export default AuthStore;
