import {action, computed, decorate, observable} from "mobx";

class CartStore {
    cart = localStorage.getItem("cart");
    expanded = false;
    length = null;
    expand () {
        this.expanded = !this.expanded;
    }
    setLength (length) {
        this.length =  length
    }
}

decorate(CartStore, {
    cart: observable,
    expanded: observable,
    expand: action,
    setLength: action
});
export default CartStore;